        ),
        5 to KeySet(
            "".decodeBase64String(),
            "".decodeBase64String()
        )
    )
    // This key pair is from Grasscutter
    val signKeySet = KeySet(
        "<RSAKeyValue><Modulus></Modulus><Exponent>AQAB</Exponent></RSAKeyValue>",
        "<RSAKeyValue><Modulus></Modulus><Exponent>AQAB</Exponent><D></D><P>8tHjikSSZN18ggXxm3MGJV8Nnb1tP3onQJZcZXOnzHptK7knmOWzuw/wMRyMZnq8ewsY6+Rw3HNydHeX/kc7PpMi69W5SbfpvWMeW2rXFlK2MZ4pmzWKGElK7aUgD5OsrwUJGcoBEnS6CFcY1kUi2B4zbfRKCOnZEvghJcnvbhc=</P><Q>0HJLZHA2lRi+QJJkdIWdAz+OrWOV3HD7SniMAalYuKURoD/zFZSdmucKs8UX+32WWlt1NH90Ijye0gwDLZ0fghQfJgpRqHIdLMIBQ0qlLSzjfeSfmHL20a+fuPK44nh2T0WjU8hkzup/OaR0IFtfc0XZManM69tgYkccLeyxWvc=</Q><DP>0ckOik32INjOklNqS0BURgNaczbOZTI3KXD+wNPsXBhFq6nbERkbb/k0LmoYzw0pPDD5Rgxmib/gWcldct29zLE4UYKkA5G2it5QwvCKhYnOSQ35qlPWTGc+KhUonuyaG9gA5dwFkxlwBHajSbQPh6KIEm4lbJAE8IOZt9lAV98=</DP><DQ>qlyvh7A6vBLT87xyA9XsJOp+NvIMWnWwvAXYD8eTrp2i0UFS8FFdmmu4kILGfhH/n2veWADPLugyueN9eXtQdCTz7EhEwxI5DAqns5K/ezOT3qHLWnKjjW8ncKZYOyhPMazttx0yXvbC8p6ZFpT3ZyQwRmnMBPxwQwJxYotvzLM=</DQ><InverseQ>MibG8IyHSo7CJz82+7UHm98jNOlg6s73CEjp0W/+FL45Ka7MF/lpxtR3eSmxltvwvjQoti3V4Qboqtc2IPCt+EtapTM7Wo41wlLCWCNx4u25pZPH/c8g1yQ+OvH+xOYG+SeO98Phw/8d3IRfR83aqisQHv5upo2Rozzo0Kh3OsE=</InverseQ></RSAKeyValue>"
    )
    /* ktlint-enable max-line-length */

    suspend fun getCertsFromConfigOrGenerate(): KeyStore = withContext(Dispatchers.IO) {
        val dispatchConfig = DispatchConfig.data
        val keyStoreFile = File(dispatchConfig.certification.keyStoreFilePath)
        if (!keyStoreFile.exists()) {
            val alias: String = DEFAULT_ALIAS
            val privateKeyPassword: String = DEFAULT_CERT_PASSWORD
            val keyStorePassword: String = DEFAULT_KEY_STORE_PASSWORD
            val expiredDays: Long = DEFAULT_EXPIRED_DAYS
            val generatedKeyStore = buildKeyStore {
                keyStore = defaultKeyStoreType
                certificate(alias) {
                    hash = HashAlgorithm.SHA256
                    sign = SignatureAlgorithm.RSA
                    keySizeInBits = 2048
                    password = privateKeyPassword
                    daysValid = expiredDays
                    hosts = listOf("localhost", ANIME_URL_1, ANIME_URL_2, ANIME_URL_3)
                    keyType = KeyType.Server
                }
            }
            generatedKeyStore.saveToFile(keyStoreFile, keyStorePassword)
            generatedKeyStore.saveCertToFile(
                File(keyStoreFile.parentFile, keyStoreFile.nameWithoutExtension + ".cert"), alias
            )
            dispatchConfig.certification = DispatchConfig.Certification(
                keyStore = defaultKeyStoreType,
                keyAlias = alias,
                keyStorePassword = keyStorePassword,
                privateKeyPassword = privateKeyPassword
            )
            DispatchConfig.save()
            generatedKeyStore
        } else {
            fromCertFile(keyStoreFile, dispatchConfig.certification.keyStorePassword)
        }
    }

    private fun fromCertFile(certFile: File, password: String): KeyStore {
        return KeyStore.getInstance(DispatchConfig.data.certification.keyStore)?.let {
            it.load(certFile.inputStream(), password.toCharArray())
            it
        } ?: throw IllegalStateException("Failed to load key store")
    }

    data class KeySet(
        val publicKey: String,
        val privateKey: String,
    )
}
